INSERT INTO `recettes` VALUES (1,'Poulet Pane','{\"1\": \"Indisponible :(\"}','https://drive.google.com/file/d/1axqFk8cra3GmicWI-u5h9A6oRfduJqaK/view?usp=drivesdk','none','0','Friture'),(2,'Couscous Nkui aux legumes','{\"1\": \"Couscous ma\\u00efs  : Faire bouillir environ 10 litres d\'eau\", \"2\": \"tamiser la farine de ma\\u00efs en s\\u00e9parant le son de la farine fine\", \"3\": \"laver le son\", \"4\": \"Diviser en 2 la quantit\\u00e9 d\'eau bouillante\", \"5\": \"laisser bouillir une moiti\\u00e9 sur feu et y mettre le son lav\\u00e9\", \"6\": \"remuer sans cesse jusqu\'\\u00e0 obtention d\'une bouillie homog\\u00e8ne\", \"7\": \"ajouter la farine de ma\\u00efs sous un feu vif et remuer \\u00e9nergiquement jusqu\'\\u00e0 obtention d\'une p\\u00e2te homog\\u00e8ne\", \"8\": \"ajouter une quantit\\u00e9 d\'eau chaude et recouvrir la marmite\", \"9\": \"baisser le feu\", \"10\": \"Nkui : Nettoyer les tiges de Nkui en les \\u00e9pluchant\", \"11\": \"rincer la peau ainsi obtenue et la faire plonger dans de l\'eau chaude pendant une quinzaine de minutes\", \"12\": \"l\'extraire et la p\\u00e9trir avec les mains dans un r\\u00e9cipient contenant un peu d\'eau ti\\u00e8de afin d\'obtenir un liquide bien gluant et \\u00e9pais\", \"13\": \"y ajouter les condiments bien \\u00e9cras\\u00e9s\", \"14\": \"agiter \\u00e9nergiquement la sauce pour la rendre homog\\u00e8ne\", \"15\": \"ajouter du sel et du piment \\u00e0 volont\\u00e9\", \"16\": \"Servir le couscous bien chaud accompagn\\u00e9 de la sauce\", \"17\": \"Ce met est tr\\u00e8s pris\\u00e9 chez la femme bamil\\u00e9k\\u00e9\", \"18\": \"surtout chez les accouch\\u00e9es\", \"19\": \"elles se nourrissent essentiellement de lui pendant un mois entier\", \"20\": \"Selon la tradition\", \"21\": \"l\'accouch\\u00e9e ne mange autre chose que \\u00e7a\", \"22\": \"et elle ne touche pas \\u00e0 sa nourriture\", \"23\": \"On lui alloue une co\\u00e9pouse qui la nourrit\"}','https://drive.google.com/file/d/1ZxklR34jqJNM_KmgU6f2YlK4n7kNFqtP/view?usp=drivesdk','none','0','Legume'),(3,'Eru','{\"1\": \"Indisponible :(\"}','https://drive.google.com/file/d/1doGvA32uB9Oid26BlQu98HnnN3tZN5O-/view?usp=drivesdk','none','0','Legume'),(4,'Sauce Aubergine','{\"1\": \"Cuire d\'abord les aubergines apr\\u00e8s refroidissement\", \"2\": \"Enl\\u00e8ver la peau\", \"3\": \"et l\'int\\u00e9rieur pour d\\u00e9couper ensuite ;  ajouter dans  les condiments cuits et assaisonn\\u00e9s\", \"4\": \"Laisser mijoter et  c\'est pr\\u00eat\", \"5\": \"Le compl\\u00e9ment de votre choix\"}','https://drive.google.com/file/d/1oAQTRCiUk3MBgKLW3kckORma5r895FED/view?usp=drivesdk','none','0','Sauce'),(5,'Koki','{\"1\": \"Tr3mper les grains de koki 4 \\u00e0 6h\", \"2\": \"Les laver afin de les d\\u00e9barrasser de leur peau\", \"3\": \"Dans un mixeur\", \"4\": \"\\u00e9craser les grains de koki bien lav\\u00e9s\", \"5\": \"Verser la p\\u00e2te obtenue dans un mortier en bois\", \"6\": \"Travailler la p\\u00e2te  avec une pinc\\u00e9e de sel \\u00e0 l\'aide d\'un pilon\", \"7\": \"Elle doit doubler de volume\", \"8\": \"Plus vous travaillerez votre p\\u00e2te\", \"9\": \"plus votre koki sera l\\u00e9ger\", \"10\": \"Ajouter \\u00e0 la p\\u00e2te l\'huile de palme ti\\u00e8de\", \"11\": \"M\\u00e9langer d\\u00e9licatement jusqu\'\\u00e0 obtention d\'une p\\u00e2te homog\\u00e8ne\", \"12\": \"Avant d\'ajouter de l\'eau dans votre p\\u00e2te\", \"13\": \"poser une marmite au feu avec un peu d\'eau\", \"14\": \"( ce processus permettra d\'\\u00e9viter que le koki ne se divise en deux: bas blanc et haut color\\u00e9 )\", \"15\": \"Dans un plat creux\", \"16\": \"superposer les feuilles de bananier ramollies et bien lav\\u00e9es\", \"17\": \"Ajouter \\u00e0 la p\\u00e2te l\'eau ti\\u00e8de\", \"18\": \"Bien m\\u00e9langer et v\\u00e9rifier l\'aissaisonnement\", \"19\": \"\\u00c0 l\'aide d\'une louche\", \"20\": \"ramasser la p\\u00e2te et la verser dans les feuilles de bananier\", \"21\": \"et former des boules\", \"22\": \